import {createContext, useReducer} from 'react';

export const ChaptersContext=createContext()

export const chapterReducer=(state,action)=>{
    switch(action.type){
        case 'SET_COURSE':
            return{
                Chapters:action.payload
            }
    }
}

export const ChapterContextProvider=({children})=>{
    const [state,dispatch]=useReducer(ChaptersReducer,{
        chapters:null
    });

    return(
        <ChaptersContext.Provider value={{c}}>
            {children}
        </ChaptersContext.Provider>
    )
}
