import { Authcontext } from "../context/AuthContext";
import { useContext } from "react";



export const use=()=>{
    const context=useContext(Authcontext)

    if(!context){
        throw Error('useAuthContext must be used inside an AuthContextProvider')
    }

    return context;
}
