import { useAuthContext } from "./use";

export const useLogout=()=>{
    const { dispatch }= useAuthContext();
    
    const logout=()=>{
        
        //remove user from storage
        localStorage.removeItem('user')

        //dipatch logout action
        dispatch({type:'LOGOUT'})

    }

    return {logout}
}
